mapper.insert:
INSERT
  INTO USER(
       user_name
     , password
     , first_name
     , last_name
     , age
     , salary
     ) VALUES (
       #{user_name}
     , #{password}
     , #{first_name}
     , #{last_name}
     , #{age}
     , #{salary}
     )

mapper.update:
UPDATE user
   SET first_name = #{first_name}
     , last_name = #{last_name}
     , age = #{age}
     , salary = #{salary}
 WHERE user_id = #{user_id}
